const Destination = () => {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const navigate = useNavigate();

  const handleFilterChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const handleImageClick = (mapLink) => {
    const goToMap = window.confirm(
      "Do you want to view this location on the map? Click OK for Map, Cancel for Contact Page."
    );
    if (goToMap) {
      window.open(mapLink, "_blank");
    } else {
      navigate("/contact");
    }
  };

  const filteredDestinations =
    selectedCategory === "All"
      ? destinations
      : destinations.filter((place) => place.category === selectedCategory);

  return (
    <div className="destination">
      <h1>Popular Destinations</h1>
      <p>Tours give you the opportunity to see a lot within a time frame.</p>
      <div className="filter-container">
        <label>Filter by Category: </label>
        <select value={selectedCategory} onChange={handleFilterChange}>
          <option value="All">All</option>
          <option value="Nature">Nature</option>
          <option value="Beach">Beach</option>
          <option value="Historical">Historical</option>
          <option value="City">City</option>
        </select>
      </div>
      {filteredDestinations.map((place, index) => (
        <div className={`destination-block ${index % 1 === 0 ? "left" : "right"}`} key={index}>
          <div className="des-text">
            <h2>{place.title}</h2>
            <p>{place.description}</p>
          </div>
          <div className="image">
            {place.images.map((img, imgIndex) => (
              <img key={imgIndex} alt={place.title} src={img} onClick={() => handleImageClick(place.mapLink)} className="clickable" />
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Destination;
